import React , {useState, useEffect, useCallback} from "react";
import axios from 'axios';

export default function SearchEngine({ city, setCity,setWeather }){

    
    let [loaded, setLoaded] = useState(false);

    
    const showResult = useCallback((response) => {
        setLoaded(true);
        setWeather({
            temperature: response.data.main.temp,
            icon: `http://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`,
        });
    }, [setWeather]);

    const searchCityWeather = useCallback((cityName) =>{
         let url = `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=2b6fdad0cbd018949c50c70f72250726&units=metric`;
        axios.get(url).then(showResult);
    }, [showResult]);


    function handleSubmit(event){
        event.preventDefault();
        searchCityWeather(city);
        

    }
    function handleCity(event){
        setCity(event.target.value)

    }
    useEffect(() => {
        if (city) {
            searchCityWeather(city);
    }
    }, [city, searchCityWeather]);

    
    return(
        <div>
            <form className="searchContainer" onSubmit={handleSubmit}>
                <input type="text" placeholder="Enter a city.." onChange={handleCity}></input>
                <button type="submit"> ðŸ”Ž </button>
            </form>
        </div>
    );
}
